exports.require = function (filename) {
	return function (cb) {
		try {
			var ret = require(filename)(cb, 0);
			cb(ret);
		} catch (e) {
		}
	}
}

exports.fileFunc = function (func) {
	return function (cb) {
		try {
			var ret = func(cb, 0);
			cb(ret);
		} catch (e) {
//			console.log(e);
		}
	}
}

exports.func = function (fn) {
	return function () {
		var args = Array.prototype.slice.call(arguments, 0);
		return function (cb) {
			if (!cb) {
				cb = function () {};
			}
			try {
				var ret = fn.apply(fn, args)(cb, 0);
				cb(ret);
			} catch (e) {
			}
		}
	}
}

